import "../index.css";
import { Link, useParams, useNavigate } from "react-router-dom";
import GlassesImg from "../oculos.jpeg";
import { useEffect, useState } from "react";
import mockAPI from  "../API_middlewares/mock";

function GlassesDescription(props) {
	const [glassesInfo, setGlassesInfo] = useState({});

	const glassesId = useParams();
	const navigate = useNavigate();

	useEffect(() => {
		(async () => {
			try {
				const glasses = await mockAPI.getProduct(glassesId); 
				setGlassesInfo(JSON.parse(glasses));
			} catch (exception) {
				alert(exception);
			}
		})();
	}, [])

	function addToCart(event) {
		let jsonCart = sessionStorage.getItem("cart");
		if (typeof jsonCart !== "string") {
			jsonCart = "{}";
		}
		const cart = {...JSON.parse(jsonCart)};
		cart[glassesId.id] = {
			"glassesPreviewProps": {
				name: glasses.name,
				price: glasses.price,
				img: glasses.img,
			}, 
			"qtt": 1
		}
		sessionStorage.setItem("cart", JSON.stringify(cart));
		navigate("/");
	};

	return (
		<div className=" flex-box flex-box-wrap">
			<img src={GlassesImg} className="img-oculos"/>
		  		<div className="info-oculos">
					<h1> {glasses.name} </h1>
					<h3> {glasses.marca} </h3>
					<p>
						{glasses.description}
					</p>
		  		</div>
			<div className="pagamento">
				<h2>{`Vendidos:   ${glasses.soldQtt}`}</h2>
				<h2>{`Em estoque: ${glasses.availableQtt}`}</h2>
				<h1>{`${glasses.price}`}</h1>
				<button className="pink-background" onClick={addToCart}> Adicionar ao carrinho </button>
		  	</div>
		</div>
	);
};

export default GlassesDescription;
