import "../index.css";
import { Link } from "react-router-dom";
import GlassesImg from "../oculos.jpeg";

function GlassesPreview(props) {
	function removeFromCart(event) {
		const cart = sessionStorage.getItem("cart");
		console.log(cart);
		delete cart[props.id];
		sessionStorage.setItem("cart", cart);
		//props.setCart(cart);
	}

	return (
		<div className="flex-box flex-box-wrap">
			<div className="list-item-img-wrapper">
				<img src={props.img}/>
			</div>
			<div className="v-middle">{props.name} </div>
			<div className="v-middle">{`Preço: R$${props.cost}`}</div>
			<div className="v-middle"> {props.qtt} </div>
			<button className="" onClick={removeFromCart}> X </button>
		</div>
	);
}

function GlassesPreviewList(props) {
	const items = Object.entries(props.cart).map((elem) => {
		return <GlassesPreview 
			name={`Algum óculos ${elem[0]}`} 
			cost={"300.00"} 
			img={GlassesImg} 
			key={elem[0]} 
			id={elem[0]} 
			qtt={elem[1]} 
			cart={props.cart} 
			setCart={props.setCart}
		/>
	});
	return (
		<div className="list">
			{items}
		</div>
	);
};

function Cart(props) {
	return (
		<div>
			<h1> Seu carrinho: </h1>
			<GlassesPreviewList cart={props.cart} setCart={props.setCart}/>
			<div>{`Valor Total: R$${100}`}</div>
			<Link to="/pay"> Finalizar Compra </Link>
		</div>	
	);
};

export default Cart;
