	return (
		<div className="cart-entry">
			<div className="cart-entry-qtt">
				<input type="number" value={qtt} onChange={onQttChange}/>
			</div>
			<HorizontalGlassesPreview {...glassesPreviewProps}/>
		</div>
	)
}

