export const ValidatedInput = ({id, type, label, state, onChange}) => { 
	return (
		<div>
			<label htmlFor={id}> {`${label}:`} </label>
			<input className={!state.status ? "erro" : ""} id={id} type={type} value={state.value} onChange={onChange}/>
			<div className={!state.status ? "erro" : "hidden"}> {state.error} </div>
		</div>
	);
}
