import "../index.css";
import { Link } from "react-router-dom";
import { useEffect } from "react";
import mockAPI from "../API_middlewares/mock";

function UpdateInfoForm() {
	const [name, setName] = useState({status: false, value: "", error: "Campo Obrigatório!"});
	const [email, setEmail] = useState({status: false, value: "", error: "Campo Obrigatório!"});
	const [password, setPassword] = useState({status: false, value: "", error: "Campo Obrigatório!"});
	const [telefone, setTelefone] = useState({status: false, value: "", error: "Campo Obrigatório!"});
	const navigate = useNavigate();

	return (
		<div>
			<h1>
				Olá, &lt;Usu&aacute;rio&gt;
			</h1>
			<form>
				<label htmlFor="nome">Nome:</label>
				<input id="nome" type="text"/>

				<label htmlFor="email">Email:</label>
				<input id="email" type="email"/>

				<label htmlFor="senha">Senha:</label>
				<input id="senha" type="password"/>

				<label htmlFor="telefone">Telefone:</label>
				<input id="telefone" type="tel"/>

				<input type="submit" value="Atualizar Cadastro" className="pink-background"/>
			</form>
		</div>
	);
}

function UpdateInfo(props) {
	const [name, setName] = useState({status: false, value: "", error: ""});
	const [email, setEmail] = useState({status: false, value: "", error: ""});
	const [password, setPassword] = useState({status: false, value: "", error: ""});
	const [telefone, setTelefone] = useState({status: false, value: "", error: ""});
	const [didLoad, setDidLoad] = useState(false);

	useEffect(async () => {
		try {
			const info = JSON.parse(await mockAPI.getUserInfo(props.loggedIn));
			setName(info.name);
			setEmail(info.email);
			setPassword(info.password);
			setTelefone(info.telefone);
			setDidLoad(true);
		} catch (exception) {
			setDidLoad(false);
		}
	}, [didLoad, name, email, password, telefone]);

	return (
		<UpdateInfoForm 
			initialName={name} 
			initialEmail={email} 
			initialPassword={password}
			initialTelefone={telefone}
			loaded={didLoad}
			setLoggedIn={props.setLoggedIn}
		/>
	);
};

export default UpdateInfo;
