    const [color, setColor] = useState("#3f30ca");
    return (
        <div className="icon flex-box" onClick={callback}>
            {/* <Icon icon={icon} color={color} width="40" height="40"/> */}
            <Icon className={hoverClass? hoverClass : ''} icon={icon} width="40" height="40"/>
            {/* <div onMouseEnter={() => setColor("#94A7AE")} onMouseLeave={() => setColor("#3f30ca")}>
            </div> */}
        </div> 
    )
}

export default IconButton;
