import "../index.css";
import { Link } from "react-router-dom";
import GlassesImg from "../oculos.jpeg";
import { useEffect, useState, useCallback } from "react";
import mockAPI from "../API_middlewares/mock";
import glassImg from "../"

function Glasses(props) {
	return (
		<div>
			<Link className="grid-item block-link light-gray-background" to={`/oculos/${props.name}`}>
				<div className="margin-v-25">
					{`${props.name}`}<br />
					<div className="grid-item-img-wrapper">
						<img src={props.img}/>
					</div>
					<br/>{`Preço: R$${props.price}`}
				</div>
			</Link>
		</div>
	);
}

defaultGlasses = [
	{
		"availableQtt": 33,
		"category": "redondo",
		"description": "uma descrição do óculos.",
		"img": <img src="glassImg"/>,
		"marca": "marca do óculos",
		"name": "óculos1",
		"price": 100,
		"soldQtt": 0
	},
	{
		"availableQtt": 33,
		"category": "redondo",
		"description": "uma descrição do óculos.",
		"img": <img src="glassImg"/>,
		"marca": "marca do óculos",
		"name": "óculos2",
		"price": 100,
		"soldQtt": 0
	}
]

function GlassesList(props) {
	const [glassesList, setGlassesList] = useState([]);

	useCallback(async (event) => {
		try {
			for (let i = 0; i < 8; i++) {
				await mockAPI.addProduct(defaultGlasses[i]);
			}
			await mockAPI.addProduct({
				name: name.value,
				marca: marca.value,
				price: parseFloat(cost.value),
				description: description.value,
				img: img.value,
				availableQtt: parseInt(availableQtt.value),
				category: category.value,
				soldQtt: 0
			});
		} catch (exception) {
			alert(exception);
		}
	});

	useEffect(() => {
		(async () => {
			try {
				const glasses = await mockAPI.getProducts(props.category);
				setGlassesList(JSON.parse(glasses));
			} catch(exception) {
				alert("Erro ao selecionar produtos!");
			}
		})(); 
	}, [props.category]);

	const items = Object.values(glassesList).map((elem) => {
		return <Glasses name={elem.name} price={elem.price} img={elem.img} key={elem.name} id={elem.name}/>
	});
	return (
		<div className="grid">
			{items}
		</div>
	);
};

export default GlassesList;
