import "../index.css";
import { Link, useSearchParams, useNavigate } from "react-router-dom";
import GlassesImg from "../oculos.jpeg";
import { useState } from "react";
import HorizontalGlassesPreview from "./glasses_preview";
import EditableList  from "./editable_list";



const glasses = [
	{
		name: "Algum óculos 0",
		price: 300.00,
		img: GlassesImg,
	}, {
		name: "Algum óculos 0",
		price: 300.00,
		img: GlassesImg,
	}, {
		name: "Algum óculos 0",
		price: 300.00,
		img: GlassesImg,
	}, {
		name: "Algum óculos 0",
		price: 300.00,
		img: GlassesImg,
	},
]


function CartEntry({glassesPreviewProps, qtt, onQttChange}) {
	return (
		<div className="cart-entry">
			<div className="cart-entry-qtt">
				<input type="number" value={qtt} onChange={onQttChange}/>
			</div>
			<HorizontalGlassesPreview {...glassesPreviewProps}/>
		</div>
	)
}

function Cart(props) {
	const cart = sessionStorage.getItem("cart");

	const [cartEntryProps, setCartEntryProps] = useState({
		"Algum óculos 0": {
			"glassesPreviewProps": {
				name: "Algum óculos 0",
				price: 300.00,
				img: GlassesImg,
			}, "qtt": 1,
			"onQttChange": (event) => {
				const newObj = {...cartEntryProps};
				newObj["Algum óculos 0"].qtt = parseInt(event.target.value);
				setCartEntryProps(newObj);
			}
		},
		"Algum óculos 1": {
			"glassesPreviewProps": {
				name: "Algum óculos 1",
				price: 300.00,
				img: GlassesImg,
			}, "qtt": 1,
			"onQttChange": (event) => {
				const newObj = {...cartEntryProps};
				newObj["Algum óculos 1"].qtt = parseInt(event.target.value);
				setCartEntryProps(newObj);
			}
		},
		"Algum óculos 2": {
			"glassesPreviewProps": {
				name: "Algum óculos 2",
				price: 300.00,
				img: GlassesImg,
			}, "qtt": 1,
			"onQttChange": (event) => {
				const newObj = {...cartEntryProps};
				newObj["Algum óculos 2"].qtt = parseInt(event.target.value);
				setCartEntryProps(newObj);
			}
		},
		"Algum óculos 3": {
			"glassesPreviewProps": {
				name: "Algum óculos 3",
				price: 300.00,
				img: GlassesImg,
			}, "qtt": 1,
			"onQttChange": (event) => {
				const newObj = {...cartEntryProps};
				newObj["Algum óculos 3"].qtt = parseInt(event.target.value);
				setCartEntryProps(newObj);
			}
		},
		"Algum óculos 4": {
			"glassesPreviewProps": {
				name: "Algum óculos 4",
				price: 300.00,
				img: GlassesImg,
			}, "qtt": 1,
			"onQttChange": (event) => {
				const newObj = {...cartEntryProps};
				newObj["Algum óculos 4"].qtt = parseInt(event.target.value);
				setCartEntryProps(newObj);
			}
		},
	});

	console.log(cartEntryProps);

	const removeCallback = (key) => {
		const newObj = {...cartEntryProps};
		delete newObj[key];
		setCartEntryProps(newObj);
	};

	const navigate = useNavigate();

	return (
		<>
			<div>
				<h1> Seu carrinho </h1>
				<hr/>
				<EditableList itemsProps={cartEntryProps} ItemsComponent={CartEntry} removeCallback={removeCallback}/>
				<div className="flex-box">
					<h3>
						Valor Total:
					</h3>
					<h3>
						{`R$${Object.values(cartEntryProps).reduce((acc, elem) => acc + elem.glassesPreviewProps.price, 0).toFixed(2)}`}
					</h3>
				</div>
				<div className="button pink-background">
					{props.loggedIn.length !== 0 &&
						<Link to="/pay" className="pink-background"> Finalizar Compra </Link>
					}
					{props.loggedIn.length === 0 &&
						<Link to="/login" className="pink-background"> Finalizar Compra </Link>
					}
				</div>
			</div>
		</>
	);
};

export default Cart;
