import "../index.css";
import { Icon } from '@iconify/react';
import { useState } from "react";

// const IconButton = ({icon, hoverClass, callback}) => {
const IconButton = ({icon, hoverClass, callback, colorClass}) => {
    const [color, setColor] = useState("#3f30ca");

    hoverClass = hoverClass? hoverClass : ''
    colorClass = colorClass? colorClass : ''
    return (
        <div className={`icon-div ${colorClass}`}>
            <Icon className={`icon ${hoverClass}`} icon={icon} width="40" height="40" onClick={callback}/>
        </div>
    )
}

export default IconButton;
