import "../index.css";
import { Link, useNavigate } from "react-router-dom";
import mockAPI from "../API_middlewares/mock";

function Pay(props) {
	const navigate = useNavigate();

	let cart = sessionStorage.getItem("cart");
	if (typeof cart !== "string") {
		cart = "{}";
	}
	cart = JSON.parse(cart);

	const executePurchase = async () => {
		try {
			await mockAPI.processPayment(cart);
			alert("compra realizada com sucesso.")
			sessionStorage.removeItem("cart");
			navigate("/");
		} catch(exception) {
			alert(exception);
			navigate(-1);
		}
	}

	return (
		<div>
			<h1> Pagamento </h1>
			<form onSubmit={executePurchase}>
				<label htmlFor="CPF"> CPF: </label>
				<input id="cpf" type="text"/>

				<label htmlFor="nroCartao"> Número do Cartão: </label>
				<input id="nroCartao" type="text"/>

				<label htmlFor="codSeg"> Código de Segurança: </label>
				<input id="codSeg" type="text"/>

				<label htmlFor="endereco"> Endereço: </label>
				<input id="endereco" type="text"/>

				<div> {`Valor Total: R$${props.value}`} </div>

				<input type="submit" value="Comprar" className="pink-background"/>
			</form>
		</div>
	);
};

export default Pay;
