import "../index.css";
import { Icon } from '@iconify/react';
import { useState } from "react";

// const IconButton = ({icon, hoverClass, callback}) => {
const IconButton = ({icon, hoverClass, callback}) => {
    const [color, setColor] = useState("#3f30ca");
    return (
        <Icon style={'text-align:center'} className={hoverClass? hoverClass : ''} icon={icon} width="40" height="40" onClick={callback}/>
    )
}

export default IconButton;
